import React, {useState} from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";

// components

export default function CardSettings() {
  const AddDetails = () => {

    const [criminal_name,setCriminal_name]=useState("")
    const [criminal_age,setCriminal_age]=useState("")
    const [criminal_image,setCriminal_image]=useState(null)
    const [status,setStatus]=useState("")
    const [crime_committed,setCrime_committed]=useState("")

    const history = useHistory()

    const AddCriminalDetail = async () => {
      let formField=new FormData()

      formField.append('criminal_name', criminal_name)
      formField.append('criminal_age', criminal_age)
      if(criminal_image!=null){
          formField.append('criminal_name', criminal_name)
      }
      formField.append('Status', status)
      formField.append('crime_committed', crime_committed)

      await axios({
        method: 'post',
        url: 'http://127.0.0.1:8000/api/criminal-details/',
        data: formField
      }).then((response) => {
        console.log(response.data);
        history.push('/')
      })
    }

  }
}
